INSERT INTO background_equipment(background_id, value) VALUES
((SELECT id FROM background WHERE code = 'Entertainer'), 'Выберите А или Б: А) Музыкальный инструмент (тот, что вы выбрали выше), 2 костюма, стальное зеркало, духи, одежда путешественника, 11 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Noble'), 'Выберите А или Б: А) Парадная одежда, игровой набор (тот, что был выбран выше), духи, 29 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Sailor'), 'Выберите А или Б: А) Кинжал, инструменты навигатора, верёвка, одежда путешественника, 20 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Sage'), 'Выберите А или Б: А) Боевой посох, книга (история), инструменты каллиграфа, роба, пергамент (8 листов), 8 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Hermit'), 'Выберите А или Б: А) Боевой посох, спальник, лампа, масло (3 фляги), книга (философия), одежда путешественника, набор травника, 16 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Scribe'), 'Выберите А или Б: А) Инструменты каллиграфа, парадная одежда, лампа, масло (3 фляги), пергамент (12 листов), 23 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Acolyte'), 'Выберите А или Б: А) Инструменты каллиграфа, книга (молитвенник), священный символ, пергамент (10 листов), роба, 8 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Criminal'), 'Выберите А или Б: А) 2 кинжала, воровские инструменты, лом, 2 сумки, одежда путешественника, 16 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Guide'), 'Выберите А или Б: А) Короткий лук, 20 стрел, инструменты картографа, спальник, колчан, палатка, одежда путешественника, 3 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Artisan'), 'Выберите А или Б: А) Инструменты ремесленника (выбранный вами выше), 2 сумки, одежда путешественника, 32 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Soldier'), 'Выберите А или Б: А) Копьё, короткий лук, 20 стрел, игровой набор (выбранный выше), комплект целителя, колчан, одежда путешественника, 14 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Guard'), 'Выберите А или Б: А) Копьё, лёгкий арбалет, 20 болтов, игровой набор (тот, что был выбран выше), закрытый фонарь, кандалы, колчан, одежда путешественника, 12 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Wayfarer'), 'Выберите А или Б: А) 2 кинжала, воровские инструменты, игровой набор (любой), спальник, 2 сумки, одежда путешественника, 16 зм; Б) 50 зм'),
((SELECT id FROM background WHERE code = 'Merchant'), 'Выберите А или Б: А) Инструменты навигатора, 2 сумки, одежда путешественника, 22 зм; Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Farmer'), 'Выберите А или Б: А) Серп, инструменты плотника, комплект целителя, железный горшок, лопата, одежда путешественника, 30 зм, или Б) 50 зм.'),
((SELECT id FROM background WHERE code = 'Charlatan'), 'Выберите А или Б: А) Набор для фальсификации, костюм, парадная одежда, 15 зм; Б) 50 зм.');