INSERT INTO character_class (code, name, description) VALUES
('Bard', 'Бард', 'Читайте заклинания, которые вдохновляют и исцеляют союзников или очаровывают врагов. Затем присоединитесь к Коллегии...'),
('Barbarian', 'Варвар', 'Врывайтесь в бой, охваченные Яростью. Затем следуйте Пути...'),
('Fighter', 'Воин', 'Овладейте всеми видами оружия и доспехов. Затем воплотитесь в...'),
('Wizard', 'Волшебник', 'Изучайте арканную магию и овладевайте заклинаниями для любых целей. Затем станьте...'),
('Druid', 'Друид', 'Управляйте магией природы, исцеляйте, принимайте звериные облики и контролируйте стихии. Затем присоединитесь к Кругу...'),
('Cleric', 'Жрец', 'Призывайте божественную магию для исцеления, укрепления и сокрушения. Затем овладейте Доменом...'),
('Warlock', 'Колдун', 'Накладывайте заклинания, основанные на оккультных знаниях. Затем заключите пакт с покровителем...'),
('Monk', 'Монах', 'Бросайтесь в ближний бой и выходите из него, нанося быстрые и мощные удары. Затем станьте Мастером...'),
('Paladin', 'Паладин', 'Разите врагов и защищайте союзников с помощью божественной и боевой мощи. Затем свяжите себя клятвой...'),
('Rogue', 'Плут', 'Наносите смертоносные скрытые атаки, избегая опасности с помощью скрытности. Затем воплотите в себе...'),
('Ranger', 'Следопыт', 'Объедините боевые навыки, природную магию и умения выживания. Затем воплотите в себе...'),
('Sorcerer', 'Чародей', 'Используйте врожденную магию, формируя энергию по своему желанию. Затем направьте...');
INSERT INTO race (code, name, description) VALUES
('Aasimar', 'Аасимар', 'Аасимары — это смертные, которые несут искру Верхних планов в своих душах. Будь то потомки ангельских существ или те, кто был наделён небесной силой, они могут разжечь эту искру, чтобы принести свет, исцеление и ярость небес. Аасимары могут появляться в любом поселении смертных. Они похожи на своих родителей, но живут до 160 лет и имеют черты, намекающие на их небесное происхождение, такие как веснушки с металлическим блеском, светящиеся глаза, нимб или цвет кожи, как у ангелов (серебряный, опалово-зелёный или медно-красный). Эти черты начинают проявляться постепенно, становясь очевидными, когда аасимар учится полностью раскрывать свою небесную природу.'),
('Gnome', 'Гном', 'Гномы – это волшебные существа, созданные богами изобретений, иллюзий и подземной жизни. Первые гномы редко встречались с другими народами из-за своей скрытной натуры и склонности жить в лесах и норах. Несмотря на малый рост, они компенсировали это умом, сбивая хищников с толку ловушками и лабиринтами туннелей. Они также научились магии от богов, таких как Гарл Златоблеск, Бэрван Дикий Странник и Баравар Теневой Плащ, которые навещали их в облике смертных. Эта магия в конечном итоге привела к возникновению родословных лесных и скальных гномов.Гномы – это миниатюрные существа с большими глазами и заострёнными ушами, которые живут около 425 лет. Многие гномы любят ощущение крыши над головой, даже если эта «крыша» – не более чем шляпа.'),
('Goliath', 'Голиаф', 'Громадные и внушительные, голиафы — это далёкие потомки великанов. Каждый голиаф носит в себе дары первых великанов — дары, которые проявляются в различных сверхъестественных благословениях, включая способность быстро увеличиваться и временно достигать размеров своих предков великанов.Голиафы имеют физические характеристики, напоминающие их предков, великанов. Например, некоторые голиафы выглядят, как каменные великаны, в то время другие напоминают огненных великанов. Независимо от того, с какими великанами они связаны, голиафы проложили свой собственный путь в мультивселенной (свободные от внутренних конфликтов, которые веками терзали род великанов) и стремятся к вершинам, превышающим те, что достигли их предки.'),
('Dwarf', 'Дварф', 'Дварфы были созданы из земли в древние времена божеством кузнечного дела. В разных мирах его называют по-разному — Морадин, Реоркс и так далее. Этот бог наделил дварфов способностью к работе с камнем и металлом, а также склонностью к жизни под землей. Он также сделал их стойкими, как горы, с продолжительностью жизни около 350 лет.Коренастые и зачастую бородатые, первые дварфы вырезали города и крепости в горных склонах и под землёй. Их древнейшие легенды рассказывают о сражениях с монстрами, обитающими на вершинах гор и в Подземье, будь то исполинские гиганты или подземные чудовища. Вдохновленные этими историями, дварфы любой культуры часто поют о доблестных подвигах, особенно о том, как малые побеждают великих.В некоторых мирах мультивселенной первые поселения дварфов были построены в холмах или горах, и семьи, ведущие своё происхождение от этих поселений, называют себя холмовыми или горными дварфами соответственно. В сеттингах Грейхок и Сага о копье существуют такие сообщества.'),
('Dragonborn', 'Драконорождённый', 'Предки драконорождённых вылупились из яиц хроматических и металлических драконов. Согласно одной легенде, эти яйца были благословлены драконьими богами Бахамутом и Тиамат, которые хотели населить мультивселенную существами, созданными по их образу и подобию. Другая история утверждает, что драконы создали первых драконорождённых без благословения богов. Как бы то ни было, драконорождённые обрели своё место на Материальном плане.Драконорождённые выглядят как бескрылые, двуногие драконы, покрытые чешуей, с яркими глазами, крепким телосложением и рогами на голове, а их окраска и другие особенности напоминают их драконьих предков.'),
('Orc', 'Орк', 'Орки связывают своё происхождение с Груумшем, могущественным богом, который бродил по бескрайним просторам Материального плана. Груумш наделил своих детей дарами, чтобы они могли путешествовать по огромным равнинам, обширным пещерам и бурным морям и противостоять монстрам, скрывающимся там. Даже когда орки начинают поклоняться другим богам, они сохраняют дары Груумша: выносливость, решимость и способность видеть в тьме.В среднем орки высокие и широкоплечие. У них серая кожа, заострённые уши и выдающиеся нижние клыки, похожие на маленькие бивни. Молодым оркам в некоторых мирах рассказывают истории о великих путешествиях и испытаниях их предков. Вдохновлённые этими историями, многие орки задаются вопросом, когда Груумш призовёт их совершить героические подвиги древности и окажутся ли они достойны его благосклонности. Другие орки предпочитают оставить старые истории в прошлом и найти свой собственный путь.'),
('Halfling', 'Полурослик', 'Полурослики находятся под покровительством богов, ценящих жизнь, дом и семейный очаг, поэтому они стремятся к спокойным сельским местам, где семья и община помогают формировать их жизнь. Тем не менее, многие полурослики обладают смелым и авантюрным духом, который ведёт их в путешествия, давая возможность исследовать большой мир и находить новых друзей на этом пути. Их размер, сопоставимый с размером человеческого ребенка, позволяет им незаметно проходить через толпу и пробираться через узкие пространства.Любой, кто проводил время рядом с полуросликами, особенно с полуросликами-приключенцами, наверняка становился свидетелем легендарного «везения полуросликов» в действии. Когда полурослик оказывается в смертельной опасности, то кажется, что неведомая сила вмешивается и помогает ему. Многие полурослики верят в силу удачи и приписывают этот необычный дар одному или нескольким своим благожелательным богам, включая Йондаллу, Брандобариса и Чармалейн. Возможно, этот дар также способствует их долгожительству (около 150 лет).Сообщества полуросликов бывают самых разных видов. На каждый укромный уголок, спрятанный в нетронутой части мира, найдётся преступный синдикат, как, например, клан Боромар из сеттинга Эберрон, или местная банда полуросликов, как в сеттинге Тёмное солнце.Полурослики, которые предпочитают жить под землёй, иногда называются коренастыми или стойкими полуросликами. Кочевые полурослики, а также те, кто живет среди людей и других высоких народов, иногда называются легконогими или высокорослыми полуросликами'),
('Human', 'Человек', 'Встречающиеся по всей мультивселенной, люди столь же разнообразны, сколь многочисленны, и они стремятся достичь как можно большего за отведённые им годы. Их амбиции и изобретательность вызывают восхищение, уважение и страх во многих мирах.Люди разнообразны внешне, как и жители Земли, и у них множество богов. Учёные спорят о происхождении человечества, но одно из самых ранних известных скоплений людей, как говорят, произошло из Сигила — торообразном городе в центре мультивселенной, месте, где родился Общий язык. Оттуда люди могли распространиться по всем уголкам мультивселенной, неся с собой космополитизм Города дверей.'),
('Tiefling', 'Тифлинг', 'Тифлинги либо рождаются на Нижних планах, либо имеют предков исчадий оттуда. Тифлинг связан кровью с дьяволом, демоном или другим злобным существом. Эта связь с Нижними планами — зловещее наследие тифлинга, которое обещает силу, но не влияет на моральные устои тифлинга. Тифлинг сам решает, принять или отвергнуть своё зловещее наследие.'),
('Elf', 'Эльф', 'Созданные богом Кореллоном, первые эльфы могли менять свои формы по своему желанию. Они утратили эту способность, когда Кореллон проклял их за сговор с божеством Лолс, которая пыталась, но не смогла узурпировать власть Кореллона. Когда Лолс была низвергнута в Бездну, большинство эльфов отвергли её и заслужили прощение Кореллона, но то, что Кореллон у них отнял, было утрачено навсегда.Потеряв способность изменять форму по своему желанию, эльфы отступили в Страну фей, где их печаль только усилилась под влиянием этого плана. Со временем любопытство привело многих из них к исследованию других измерений, включая миры Материального плана. Эльфы обладают заострёнными ушами и не имеют растительности на лице и теле. Они живут около 750 лет и не спят, а вместо этого входят в транс, когда нуждаются в отдыхе. В этом состоянии они остаются осведомлёнными о своём окружении, погружаясь в воспоминания и медитации.Окружающая среда постепенно трансформирует эльфов, если они живут в ней более тысячи лет, и наделяет их определённой магией. Дроу, высшие эльфы и лесные эльфы — примеры эльфов, подвергшихся таким изменениям.');
INSERT INTO sub_race (race_id, code, name, description) VALUES
((SELECT id FROM race WHERE code = 'Tiefling'), 'Abyssal', 'Наследие бездны', 'Энтропия Бездны, хаос Пандемониума и отчаяние Карцери влекут тифлингов, у которых есть наследие Бездны. Рога, шерсть, клыки и необычные запахи — обычные физические черты таких тифлингов, большинство из которых имеют в своих жилах кровь демонов.'),
((SELECT id FROM race WHERE code = 'Tiefling'), 'Chthonic', 'Хтоническое наследие', 'Тифлинги с хтоническим наследием ощущают не только притяжение Карцери, но и жадность Геенны и мрак Аида. Некоторые из этих тифлингов выглядят как живые мертвецы. Другие обладают неземной красотой суккуба или имеют физические черты, общие с ночной каргой, юголотом или другим зловещим существом с Нейтрально-Злым происхождением.'),
((SELECT id FROM race WHERE code = 'Tiefling'), 'Infernal', 'Инфернальное наследие', 'Инфернальное наследие связывает тифлингов не только с Геенной, но и с Девятью преисподними и яростными полями битв Ахерона. Рога, шипы, хвосты, золотые глаза и слабый запах серы или дыма — обычные физические черты таких тифлингов, большинство из которых ведут своё происхождение от дьяволов.'),
((SELECT id FROM race WHERE code = 'Elf'), 'Drow', 'Дроу', 'Дроу обычно обитают в Подземье и были им изменены. Некоторые дроу, как отдельные индивиды, так и целые сообщества, избегают Подземья, но всё же сохраняют его магию. Например, в сеттинге Эберрон дроу живут в тропических лесах и циклопических руинах на континенте Ксен’дрик'),
((SELECT id FROM race WHERE code = 'Elf'), 'High', 'Высшие эльфы', 'Высшие эльфы пропитаны магией пересечения между Страной фей и Материальным планом. В некоторых мирах высшие эльфы называют себя другими именами. Например, в сеттинге Забытых Королевств они называют себя солнечными или лунными эльфами, в сеттинге Саги о копье — Сильванести и Квалинести, а в сеттинге Эберрон — Аэрени.'),
((SELECT id FROM race WHERE code = 'Elf'), 'Wood', 'Лесные эльфы', 'Лесные эльфы несут в себе магию первобытных лесов. Они известны под многими другими именами: дикие эльфы, зелёные эльфы и лесные эльфы. Гругачи — это скрытные лесные эльфы сеттинга Грейхок, а Кагонести и Терннадал — лесные эльфы сеттингов Сага о копье и Эберрон соответственно.');
