INSERT INTO skill (ability_id, name, description) VALUES
((SELECT id FROM ability WHERE code = 'STR'), 'Атлетика', 'Прыгнуть дальше обычного, остаться на плаву в бурной воде или сломать что-то.'),
((SELECT id FROM ability WHERE code = 'DEX'), 'Акробатика', 'Удержаться на ногах в сложной ситуации или выполнить акробатический трюк.'),
((SELECT id FROM ability WHERE code = 'DEX'), 'Ловкость рук', 'Обыскать карман, спрятать предмет в руке или выполнить фокус.'),
((SELECT id FROM ability WHERE code = 'DEX'), 'Скрытность', 'Остаться незамеченным, передвигаясь тихо и прячась за объектами.'),
((SELECT id FROM ability WHERE code = 'INT'), 'Аркана', 'Вспомнить знания о заклинаниях, магических предметах и планах бытия.'),
((SELECT id FROM ability WHERE code = 'INT'), 'История', 'Вспомнить знания об исторических событиях, людях, нациях и культурах.'),
((SELECT id FROM ability WHERE code = 'INT'), 'Анализ', 'Найти скрытую информацию в книгах или понять, как что-то работает.'),
((SELECT id FROM ability WHERE code = 'INT'), 'Природа', 'Вспомнить знания о местности, растениях, животных и погоде.'),
((SELECT id FROM ability WHERE code = 'INT'), 'Религия', 'Вспомнить знания о богах, религиозных ритуалах и священных символах.'),
((SELECT id FROM ability WHERE code = 'WIS'), 'Уход за животными', 'Успокоить или дрессировать животное, или заставить его вести себя определённым образом.'),
((SELECT id FROM ability WHERE code = 'WIS'), 'Проницательность', 'Определить настроение и намерения человека.'),
((SELECT id FROM ability WHERE code = 'WIS'), 'Медицина', 'Диагностировать болезнь или определить, что убило недавно погибшего.'),
((SELECT id FROM ability WHERE code = 'WIS'), 'Внимательность', 'С использованием комбинации чувств заметить что-то, что легко пропустить.'),
((SELECT id FROM ability WHERE code = 'WIS'), 'Выживание', 'Идти по следам, собирать съедобные растения, найти тропу или избежать природных опасностей.'),
((SELECT id FROM ability WHERE code = 'CHA'), 'Обман', 'Убедительно солгать или убедительно носить маскировку.'),
((SELECT id FROM ability WHERE code = 'CHA'), 'Запугивание', 'Внушить трепет или заставить кого-то сделать то, что вам нужно.'),
((SELECT id FROM ability WHERE code = 'CHA'), 'Выступление', 'Выступить, рассказать историю, исполнить музыку или станцевать.'),
((SELECT id FROM ability WHERE code = 'CHA'), 'Убеждение', 'Честно и вежливо убедить кого-то в чём-то.');
